mapper.delete:
DELETE 
FROM post_bookmark
WHERE post_id = #{id} AND uid = #{uid};

mapper.select:
SELECT COUNT(*) 
FROM post_bookmark
WHERE post_id = #{id} AND uid = #{uid};

mapper.update:
update post 
set title = #{title}, content = #{content}
where id = #{id}

update post 
set view_cnt = view_cnt + 1
where id = #{id}
